mapper.select:
select place.* , (select realfilename from files where tablename='place' and tablenum = place.placenum and state=1 and rownum=1) as imagename , (select count(*) as likecnt from likeplace where placenum = place.placenum) as likecnt
, (select likeplacenum from likeplace where placenum = place.placenum and membernum = #{membernum}) as likemy
from place where placenum = #{placenum}
